using UnityEngine;using UnityEditor;using System;using System.Collections;using System.Collections.Generic;using System.Linq;using System.IO;namespace Entitas.Unity.VisualDebugging {	public class DiagramWindow : EditorWindow {		LineChart entityDiagram;	 	GUIStyle boxStyle;		Vector2 scrollPos = Vector2.zero;		static DiagramWindow window;	[MenuItem ("Entitas/ShowDiagram")]	static void Init () {	       // Get existing open window or if none, make a new one:			window = (DiagramWindow) EditorWindow.GetWindow (typeof (DiagramWindow),false, "Entity Diagram");			// Listen for moves			window.wantsMouseMove = true;			// Create a style which loads a background image			window.boxStyle = new GUIStyle();			window.boxStyle.normal.background = (Texture2D) Resources.Load("Black", typeof(Texture2D));			window.boxStyle.border = new RectOffset(32,32,32,32);			window.boxStyle.margin = new RectOffset(4,4,4,4);					window.Focus();	    }			   void OnGUI () {			if(GUILayout.Button("Close Window")){				window.Close();			}			if (entityDiagram == null) {				entityDiagram = new LineChart();			}			entityDiagram.drawControls();			EditorGUILayout.BeginHorizontal(boxStyle);			scrollPos = EditorGUILayout.BeginScrollView(scrollPos);			entityDiagram.DrawChart();			EditorGUILayout.EndScrollView();			EditorGUILayout.EndHorizontal();		}	}}